  <script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.4.0/jspdf.umd.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/html2canvas/1.4.1/html2canvas.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>
  <script>
    $(".btn-circle-download").click(function() {
  $(this).addClass("load");
  setTimeout(function() {
    $(".btn-circle-download").addClass("done");
  }, 1000);
  setTimeout(function() {
    $(".btn-circle-download").removeClass("load done");
  }, 5000);
});

  </script>
 

  <script>
    window.onload = function () {
      document.getElementById('downloadBtn').addEventListener('click', function () {
        const { jsPDF } = window.jspdf;
        const certificateElement = document.querySelector('#certificate');

        html2canvas(certificateElement, { scale: 2 }).then(canvas => {
          const imgData = canvas.toDataURL('image/png');
          const imgWidth = canvas.width;
          const imgHeight = canvas.height;
          const pdf = new jsPDF('landscape', 'pt', [imgWidth, imgHeight]);

          pdf.addImage(imgData, 'PNG', 0, 0, imgWidth, imgHeight);
          pdf.save('certificate.pdf');
        });
      });
    };
  </script>

